function OEFICode0($p) { if (!is_array($p)) return '';
	if (!$p[0]) return '--'; 
$diff = time()-$p[0]; if ($diff<0) $diff = 0;
if ($diff < 60*60) return floor($diff/60).'min';
if ($diff < 60*60*24) return floor($diff/60/60).'h';
//if ($diff < 60*60*24*132) return date('d/m', $p[0]);
$r = date('d/m/y', $p[0]);
return $r;//return floor($diff/(60*60*24)).$p[1];
} //in WE601878351f..OEDynTag0.html1

function OEFICode1($p) { if (!is_array($p)) return '';
	return $p[0]-1;
} //in WE3147299e4d..OEDynTag0.html1

function OEFICode2($p) { if (!is_array($p)) return '';
	// $p[0]=curr page (first page = 1), ($p[1] not used), $p[2]=self-link, $p[3]=total pages
if ($p[3] < 2) return '1/1';

$maxPagesShowAll = 10;
$near_startend = 2;
$near_current = 2;

$r = '';
$r .= ($p[0]>1) ? ("<a href=\"{$p[2]}?oepage1=1\">&lt;&lt;</a>") : '|&lt;&lt;';
$r .= '&nbsp;&nbsp';
$r .= ($p[0]>1) ? ("<a href=\"{$p[2]}?oepage1=".($p[0]-1).'">&lt;</a>') : '&lt;';
$r .= '&nbsp;&nbsp';

$sp = '';

for ($i=1; $i<=$p[3]; $i++) {
	if ($p[3] > $maxPagesShowAll) { // too many pages to show at once
		if (!($i >= $p[0]-$near_current && $i <= $p[0]+$near_current) && 
		    !($i <= $near_startend || $i >= $p[3]+1-$near_startend)) { 
                        // not near current page or start or end
			if ($i == $near_startend+1 || $i == $p[3]-$near_startend) $sp .= ' .. '; 
                        // ex "1 2 .." and ".. 41 42"
			continue; // don't show page number
		} 
	}
	// show page number, with link if not current page
	
	$pageLink = $p[2].'?oepage1='.$i; // ex "admin.php?oepage=4"
	$pageSt   = ($i == $p[0]) ? '<b>'.$i.'</b>' : "<a href='$pageLink'>$i</a>";
	$sp .= '&nbsp;'.$pageSt.'&nbsp;';
}
$r .= $sp;

$r .= '&nbsp;&nbsp';
$r .= ($p[0]<$p[3]) ? ("<a href=\"{$p[2]}?oepage1=".($p[0]+1).'">&gt;</a>') : '&gt;';
$r .= '&nbsp;&nbsp';
$r .= ($p[0]<$p[3]) ? ("<a href=\"{$p[2]}?oepage1={$p[3]}\">&gt;&gt;</a>") : '&gt;&gt;|';

return $r;
} //in WEcf4b44b513..OEDynTag0.html1

function OEFICode3($p) { if (!is_array($p)) return '';
	return ($p[0] > 0 && $p[1] > 0) ? ceil($p[0]/$p[1]) : 1;
} //in PK1!FIListTotalPages

function OEFICode4($p) { if (!is_array($p)) return '';
	return $p[0] % 2;
} //in PK1!FIRowEven

function OEFICode5($p) { if (!is_array($p)) return '';
	return ($p[0]-1)*$p[1];
} //in PK1!FICurrListOffset

function OEFICode6($p) { if (!is_array($p)) return '';
	if (!$p[0] || !preg_match("/^.{6,20}$/", $p[0])) return null; else return 1;
} //in PK1!FIAdminDefaultPW_Validator

